import { Component } from '@angular/core';
import { CountriesService } from '../../services/countries.service';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'app-by-capitale-page',
  templateUrl: './by-capitale-page.component.html',
  styles: [
  ]
})
export class ByCapitalePageComponent {

  public countries: Country[] = [];
  constructor( private countriesService: CountriesService ){}


  searchByCapital( term:string ): void{
    this.countriesService.searchCapital( term ).subscribe( countries => {this.countries = countries})
  }
}
<h2>Por Capital</h2>
<hr>


<div class="row">
  <div class="col">
    <!-- SearchBox-->
    <shared-search-box
    (onValue)="searchByCapital($event)"
    placeholder="Buscar por capital"></shared-search-box>
  </div>
</div>

<div class="row">
  <div class="col">
    <hr>
    <countries-table [countries]="countries"></countries-table>
  </div>
</div>
